let pi10k = '3.'

// --------------------------------------------------------------------------------------------------------------------------------
let activeGroup = -1
let numLines = 0

// --------------------------------------------------------------------------------------------------------------------------------
function searchGroups () {
  let searchValue = iSearchValue.value.toString()
  if (searchValue.length === 0) return

  let searchSize = searchValue.length
  let searchStartChar = searchValue.charAt(0)
  let searchEndChar = searchValue.charAt(searchSize - 1)

  let adaptedSearchSize = searchSize - 1

  let startIndex = pi10k.indexOf(searchStartChar)
  let endIndex = 0

  let isHit = true
  let currentSubIndex = 1
  let subSearchChar = '-'

  let minHitRanges = []
  let maxHitRanges = []

  if (searchSize === 1) {
    // TODO: Handle single or two value search
    while (startIndex !== -1) {
      minHitRanges.push(startIndex)
      maxHitRanges.push(startIndex)
      startIndex = pi10k.indexOf(searchStartChar, startIndex + 1)
    }
  } else {
    while (startIndex !== -1) {
      endIndex = pi10k.indexOf(searchEndChar, startIndex + 1)
      // TODO: Run until end of file
      if (endIndex === -1) break

      while (endIndex - startIndex < adaptedSearchSize) {
        endIndex = pi10k.indexOf(searchEndChar, endIndex + 1)
        if (endIndex === -1) break
      }

      if (endIndex - startIndex === adaptedSearchSize) {
        isHit = true
        currentSubIndex = 1

        let searchString = pi10k.substr(startIndex + 1, adaptedSearchSize - 1)
        subSearchChar = searchValue.charAt(currentSubIndex++)
        for (let char of searchString) {
          if (char !== subSearchChar) {
            isHit = false
            break
          } else subSearchChar = searchValue.charAt(currentSubIndex++)
        }

        if (isHit) {
          minHitRanges.push(startIndex)
          maxHitRanges.push(endIndex)
        }
      }

      startIndex = pi10k.indexOf(searchStartChar, startIndex + 1)
    }
  }

  if (minHitRanges[0] !== undefined) {
    markRanges(minHitRanges, maxHitRanges)
    document.querySelector('#dataStats #dGroupCount').textContent = minHitRanges.length
    document.querySelector('#dataStats #dGroupFirst').textContent = (minHitRanges[0] + 1) + ' bis ' + (maxHitRanges[0] + 1)
    document.querySelector('#dataStats #dGroupEnd').textContent = (minHitRanges[minHitRanges.length - 1] + 1) + ' bis ' + (maxHitRanges[minHitRanges.length - 1] + 1)

    let percent = ((minHitRanges.length * searchSize) / (1.0 * pi10k.length) * 100).toString()
    percent = percent.substr(0, percent.indexOf('.') + 3)

    document.querySelector('#dataStats #dGroupPercent').textContent = percent + ' %'
  }

  fillLineNums(null)
}

// --------------------------------------------------------------------------------------------------------------------------------

function resetGroups () {
  resetActiveGroup()
  let highlightItems = document.querySelectorAll('#dataTab .highlight')

  for (let item of highlightItems) {
    item.removeAttribute('class')
    item.removeEventListener('mouseover', highlightGroup)
    item.removeEventListener('mouseout', unhighlightGroup)
    item.removeEventListener('click', setGroupInfo)
  }

  document.querySelector('#dataStats #dGroupCount').textContent = 0
  document.querySelector('#dataStats #dGroupFirst').textContent = '---'
  document.querySelector('#dataStats #dGroupEnd').textContent = '---'
  document.querySelector('#dataStats #dGroupPercent').textContent = '0 %'

  document.querySelector('#dataMarkerTab #dPiPos').textContent = '0 / ' + (pi10k.length + 1)
  document.querySelector('#dataMarkerTab #dPiStart').textContent = '---'
  document.querySelector('#dataMarkerTab #dPiEnd').textContent = '---'
  document.querySelector('#dataMarkerTab #dPiIndex').textContent = '---'

  activeGroup = -1
}

function setSearchValue (event) {
  let searchValue = 0
  let size = 1
  let startIndex = 0
  let endIndex = 0

  if (event === null) {
    let selection = window.getSelection()
    let anchor = selection.anchorNode
    let focus = selection.focusNode

    if (anchor === null || anchor.nodeName !== '#text' || anchor.parentNode === null || anchor.parentNode.dataset['idx'] === undefined || focus.nodeName !== '#text' || focus.parentNode === null || focus.parentNode.dataset['idx'] === undefined) {
      return
    }

    startIndex = parseInt(anchor.parentNode.dataset['idx'])
    endIndex = parseInt(focus.parentNode.dataset['idx'])

    if (startIndex !== endIndex) {
      size = (endIndex < startIndex ? startIndex - endIndex : endIndex - startIndex) + 1
    }

    if (size > 100) {
      window.alert('Die Auswahl kann maximal 100 Zahlen umfassen.')
      return
    }

    searchValue = pi10k.substr(startIndex, size)
    iSearchValue.value = searchValue
  } else if (event.target !== null) {
    searchValue = event.target.value.replace(/[\D]*/g, '')
    event.target.value = searchValue

    if (searchValue.length === 0) {
      document.querySelector('#dataMarkerTab #dSearchValue').textContent = '---'
      return
    }

    size = searchValue.length
    startIndex = -2
    endIndex = -2

    if (event.keyCode !== undefined && event.keyCode === 13) {
      document.querySelector('#menu button[data-command="searchGroups"]').click()
    }
  }

  document.querySelector('#dataMarkerTab #dSearchValue').textContent = searchValue
  document.querySelector('#dataMarkerTab #dSearchValueStart').textContent = startIndex + 2
  document.querySelector('#dataMarkerTab #dSearchValueEnd').textContent = endIndex + 2
  document.querySelector('#dataMarkerTab #dSearchValueSize').textContent = size
}

// -----------------------------------------------------------------------------------------------------------------
function bHandleCommands (event) {
  switch (event.target.dataset['command']) {
    case 'searchGroups':
      resetGroups()
      searchGroups()
      // markRanges([0, 5, 10], [3, 8, 12])
      break
    case 'ssearchval':
      setSearchValue(null)
      break
    case 'snext':
      jumpNextGroup()
      break
    case 'sprev':
      jumpPrevGroup()
      break
    case 'sfirst':
      jumpFirstGroup()
      break
    case 'slast':
      jumpLastGroup()
      break
    case 'reset':
      resetApp()
      break
    default:
      break
  }
}

function handleKeyboard (evt) {
  console.log(evt)
  console.log(evt.keyCode)
  if (evt.target.nodeName !== 'INPUT') {
    switch (evt.keyCode) {
      case 78: // n
        jumpNextGroup()
        break
      case 86: // v
      case 80: // p
        jumpPrevGroup()
        break
      case 69: // e
      case 70: // f
        jumpFirstGroup()
        break
      case 76: // l
        jumpLastGroup()
        break
      default:
        break
    }
  }
}

// -----------------------------------------------------------------------------------------------------------------
function jumpFirstGroup () {
  jumpToGroup(0)
}

// -----------------------------------------------------------------------------------------------------------------
function jumpLastGroup () {
  let groupCount = document.querySelectorAll('#dataTab .highlight.first').length - 1
  if (groupCount < 0) return
  jumpToGroup(groupCount)
}

// -----------------------------------------------------------------------------------------------------------------
function jumpToGroup (group) {
  let firstElement = document.querySelector('#dataTab .highlight.first[data-grp="' + group + '"]')
  if (firstElement === null) return

  activeGroup = group
  firstElement.click()

  dataTab.scrollTop = firstElement.offsetTop - (dataTab.clientHeight * 0.75)
}

// -----------------------------------------------------------------------------------------------------------------
function jumpNextGroup () {
  let groupCount = document.querySelectorAll('#dataTab .highlight.first').length - 1
  if (groupCount < 0) return

  if (activeGroup === -1 || activeGroup === groupCount) jumpToGroup(0)
  else jumpToGroup(++activeGroup)
}

// -----------------------------------------------------------------------------------------------------------------
function jumpPrevGroup () {
  let groupCount = document.querySelectorAll('#dataTab .highlight.first').length - 1
  if (groupCount < 0) return

  if (activeGroup <= 0) jumpToGroup(groupCount)
  else jumpToGroup(--activeGroup)
}

// -----------------------------------------------------------------------------------------------------------------

function markRanges (minTargets, maxTargets) {
  if (minTargets.length !== maxTargets.length) return

  let dataTabChildren = dataTab.children

  let currentGroup = 0
  for (let index in minTargets) {
    let startIndex = minTargets[index]
    let endIndex = maxTargets[index]

    if (startIndex === endIndex) {
      let child = dataTabChildren[startIndex]
      child.dataset['grp'] = currentGroup
      child.classList.add('highlight')
      child.classList.add('first')
      child.classList.add('last')
      child.addEventListener('mouseover', highlightGroup)
      child.addEventListener('mouseout', unhighlightGroup)
      child.addEventListener('click', setGroupInfo)
    } else {
      for (let current = startIndex; current <= endIndex; ++current) {
        let child = dataTabChildren[current]
        child.dataset['grp'] = currentGroup

        if (current === startIndex) {
          child.classList.add('highlight')
          child.classList.add('first')
        } else if (current < endIndex) {
          child.classList.add('highlight')
        } else {
          child.classList.add('highlight')
          child.classList.add('last')
        }

        child.addEventListener('mouseover', highlightGroup)
        child.addEventListener('mouseout', unhighlightGroup)
        child.addEventListener('click', setGroupInfo)
      }
    }

    ++currentGroup
  }
}

function highlightGroup (event) {
  if (event.target.classList.contains('mouseover')) return

  let groupMembers = document.querySelectorAll('#dataTab .highlight[data-grp="' + event.target.dataset['grp'] + '"]')
  for (let elm of groupMembers) {
    elm.classList.add('mouseover')
  }
}

function unhighlightGroup (event) {
  let groupMembers = document.querySelectorAll('#dataTab .highlight[data-grp="' + event.target.dataset['grp'] + '"]')

  for (let elm of groupMembers) {
    elm.classList.remove('mouseover')
  }
}

// -----------------------------------------------------------------------------------------------------------------

let controls = document.querySelectorAll('button')
for (let control of controls) {
  if (control.dataset['command'] !== undefined) {
    control.addEventListener('click', bHandleCommands)
  }
}

function resetActiveGroup () {
  let firstElement = document.querySelector('#dataTab .highlight.first.active')
  if (firstElement === null) return

  let lastElement = document.querySelector('#dataTab .highlight.last.active')

  for (let i = parseInt(firstElement.dataset['idx']), j = parseInt(lastElement.dataset['idx']) + 1; i < j; ++i) {
    dataTab.children[i].classList.remove('active')
  }
}

function setGroupInfo (event) {
  resetActiveGroup()

  let group = parseInt(event.target.dataset['grp'])
  activeGroup = group

  let firstElement = document.querySelector('#dataTab .highlight.first[data-grp="' + group + '"]')
  if (firstElement === null) return

  let lastElement = document.querySelector('#dataTab .highlight.last[data-grp="' + group + '"]')

  for (let i = parseInt(firstElement.dataset['idx']), j = parseInt(lastElement.dataset['idx']) + 1; i < j; ++i) {
    dataTab.children[i].classList.add('active')
  }

  document.querySelector('#dataMarkerTab #dPiPos').textContent = (parseInt(firstElement.dataset['idx']) + 1) + ' / ' + (pi10k.length + 1)
  document.querySelector('#dataMarkerTab #dPiStart').textContent = parseInt(firstElement.dataset['idx']) + 1
  document.querySelector('#dataMarkerTab #dPiEnd').textContent = parseInt(lastElement.dataset['idx']) + 1
  document.querySelector('#dataMarkerTab #dPiIndex').textContent = parseInt(group) + 1
}

function resetApp () {
  resetGroups()

  iSearchValue.value = ''

  document.querySelector('#dataMarkerTab #dSearchValue').textContent = '---'
  document.querySelector('#dataMarkerTab #dSearchValueStart').textContent = 0
  document.querySelector('#dataMarkerTab #dSearchValueEnd').textContent = 0
  document.querySelector('#dataMarkerTab #dSearchValueSize').textContent = 0
}

// -----------------------------------------------------------------------------------------------------------------

function scrollLineNums (event) {
  dataLineTab.scrollTop = dataTab.scrollTop
  let firstActiveLine = Math.floor(dataTab.scrollTop / dataTab.children[0].offsetHeight)
  let lastActiveLine = Math.floor((dataTab.scrollTop + dataTab.clientHeight - 28) / dataTab.children[0].offsetHeight)

  if (lastActiveLine >= dataLineTab.children.length) lastActiveLine = dataLineTab.children.length - 1

  let currentActiveLine = document.querySelectorAll('#dataLineTab .active')
  for (let line of currentActiveLine) {
    line.classList.remove('active')
  }

  dataLineTab.children[Math.floor((firstActiveLine + lastActiveLine - 1) * 0.5)].classList.add('active')
  dataLineTab.children[firstActiveLine].classList.add('active')
  dataLineTab.children[lastActiveLine].classList.add('active')
}

// -----------------------------------------------------------------------------------------------------------------

function fillDataTab () {
  let htmlDataPool = []

  for (let index in pi10k) {
    let spanElement = document.createElement('span')
    spanElement.dataset['idx'] = index
    spanElement.dataset['v'] = pi10k[index]
    spanElement.textContent = pi10k[index]
    htmlDataPool.push(spanElement.outerHTML)
  }

  dataTab.innerHTML = htmlDataPool.join('')
}

// -----------------------------------------------------------------------------------------------------------------
function highlightLine (event) {
  let lineNumber = parseInt(event.target.textContent) - 1
  if (event.target.classList.contains('highlighted')) {
    event.target.classList.remove('highlighted')

    let highlightedLines = document.querySelectorAll('#dataTab .lineHighlight[data-lh="' + lineNumber + '"]')
    for (let line of highlightedLines) line.classList.remove('lineHighlight')
    return
  }

  let highlightHeight = (lineNumber * dataTab.children[0].offsetHeight) + dataTab.children[0].offsetTop

  let charWidth = 0
  for (let child of dataTab.children) {
    if (child.classList.length === 0) {
      charWidth = child.offsetWidth
      break
    }
  }

  let approxCharsInLine = Math.round((dataTab.clientWidth / charWidth) * 0.5)

  let dataTabChildren = []
  for (let child of dataTab.children) dataTabChildren.push(child)

  let startIndex = lineNumber * approxCharsInLine
  let firstChild = -1
  let lastChild = -1

  for (let index = startIndex, count = dataTabChildren.length; index < count; ++index) {
    let child = dataTabChildren[index]

    if (child.offsetTop === highlightHeight) {
      if (firstChild === -1) firstChild = index
      child.dataset['lh'] = lineNumber
      lastChild = index
    } else if (child.offsetTop > highlightHeight) {
      if (index === startIndex) index -= approxCharsInLine * 2
      else break
    }
  }

  let htmlDataPool = []
  dataTab.innerHTML = ''

  for (let index = firstChild; index <= lastChild; ++index) {
    dataTabChildren[index].classList.add('lineHighlight')
  }

  for (let child of dataTabChildren) {
    htmlDataPool.push(child.outerHTML)
  }

  dataTab.innerHTML = htmlDataPool.join('')

  for (let child of dataTab.children) {
    if (child.dataset['grp'] !== undefined) {
      child.addEventListener('mouseover', highlightGroup)
      child.addEventListener('mouseout', unhighlightGroup)
      child.addEventListener('click', setGroupInfo)
    }
  }

  event.target.classList.add('highlighted')
}

// -----------------------------------------------------------------------------------------------------------------

function fillLineNums (event) {
  let countedLines = Math.floor(1.0 * dataTab.scrollHeight / dataTab.children[0].offsetHeight) - 1

  if (event !== null && numLines === countedLines) return

  let lines = document.querySelectorAll('#dataLineTab span')
  for (let line of lines) {
    line.removeEventListener('click', highlightLine)
  }

  let htmlDataPool = []
  let lineNumber = 0

  for (let index = 0; index < countedLines; ++index) {
    let lineElement = document.createElement('span')
    lineElement.textContent = ++lineNumber
    htmlDataPool.push(lineElement.outerHTML)
  }

  dataLineTab.innerHTML = htmlDataPool.join('')
  numLines = lineNumber

  lines = document.querySelectorAll('#dataLineTab span')
  for (let line of lines) {
    line.addEventListener('click', highlightLine)
  }

  let highlightedLines = document.querySelectorAll('#dataTab .lineHighlight')
  for (let line of highlightedLines) line.classList.remove('lineHighlight')

  scrollLineNums(null)
}

// -----------------------------------------------------------------------------------------------------------------

let dataTab = document.querySelector('#dataTab')
dataTab.style['height'] = document.querySelector('#dataMarkerTab').clientHeight + 'px'
dataTab.addEventListener('scroll', scrollLineNums)

let dataLineTab = document.querySelector('#dataLineTab')
dataLineTab.style['height'] = dataTab.clientHeight + 'px'

document.querySelector('#dataMarkerTab #dPiPos').textContent = '0 / ' + (pi10k.length + 1)

let iSearchValue = document.querySelector('#menu #iSearchValue')
iSearchValue.addEventListener('change', setSearchValue)
iSearchValue.addEventListener('keyup', setSearchValue)

document.addEventListener('keydown', handleKeyboard)
window.addEventListener('resize', fillLineNums)

fillDataTab()
fillLineNums()

// -----------------------------------------------------------------------------------------------------------------
